import { Backdrop, Button, CircularProgress } from "@mui/material";
import React, { useEffect } from "react";
import { useSelector } from "../store";
import { useParams } from "react-router-dom";
import { getDetail } from "../redux/item/itemSlice";
import { useDispatch } from "react-redux";


export const ItemDetail = () => {
  type apiDetailArray = {
    details: {
      id: number;
      title: string;
      image: string;
      price: number;
      category: string;
      description: string;
    }
  };


  const dispatch = useDispatch();
  // const details = useSelector((state) => state.items.detail)
  const {detail}= useSelector((state) => state.items);
  const details:apiDetailArray[] = detail
  const { productId } = useParams();
  const open:boolean = true;




  useEffect(() => {
    dispatch(getDetail(productId));
    console.log(detail);

  }, []);


  return (
    <div className="container">

      {detail ? (
        <div>
          {
            <div className="detail flex" key={details.id}>
              <div className="detail__image">
                <img src={detail.image} />
              </div>
              <div className="detail__title">
                <h1 className="detail__title__text">{detail.title}</h1>
                <h2 className="detail__title__text">
                  {detail.price * 100}円
                </h2>
                <h3 className="detail__title__text">カテゴリー：{detail.category}</h3>
                <p>{detail.description}</p>
                <div className="detail__title__text">
                  <Button variant="contained" className="detail__cart_btn">
                    カートに入れる
                  </Button>
                </div>
              </div>
            </div>
          }
        </div>
      ) : (
        <Backdrop open={open}>
        <CircularProgress color="inherit" />
      </Backdrop>
        // <div>...Loading</div>
      )}
    </div>
  );
};
