import React, { useEffect } from "react";
import {
  createSearchParams,
  useNavigate,
  useSearchParams,
} from "react-router-dom";

type priceState = {
  maxPriceRange: number,
  minPriceRange: number,
  setMinPriceRange: number,

}

export const Sidebar = ({ maxPriceRange, setMaxPriceRange,minPriceRange, setMinPriceRange }:priceState) => {
  const categoryLists = [
    {
      id: "0",
      category: "all",
      categoryName: "全て",
    },
    {
      id: "1",
      category: "men's clothing",
      categoryName: "メンズ",
    },
    {
      id: "2",
      category: "women's clothing",
      categoryName: "ウィメンズ",
    },
    {
      id: "3",
      category: "jewelery",
      categoryName: "アクセサリー",
    },
    {
      id: "4",
      category: "electronics",
      categoryName: "電子機器",
    },
  ];

  const navigate = useNavigate();
  const [searchParams, setSearchParams] = useSearchParams();
  let params = "";

  // カテゴリ絞り込み
  const handleChecked = (e: React.ChangeEvent<HTMLInputElement>) => {
    let checkedId = e.target.id;
    let checkedName = e.target.value;
    params = createSearchParams({
      categoryId: checkedId,
      category: checkedName,
    }).toString();
    navigate(`?${params}`, { replace: false });
  };

  useEffect(() => {
    setSearchParams(params);
  }, [params]);



  const handleMaxPrice = (e: React.ChangeEvent<HTMLInputElement>) => {
    let maxPrice = e.target.value;
    setMaxPriceRange(maxPrice);
  }

  const handleMinPrice = (e: React.ChangeEvent<HTMLInputElement>) => {
    let minPrice = e.target.value;
    setMinPriceRange(minPrice);
  }

  return (
    <div className="sidebar">
      <div className="cate_wrap">
        <p>値段で絞り込む</p>
        <div className="slidebar-multithumb">
          <input
            className="thumb-1"
            type="range"
            name="range-1"
            id="range-1"
            onChange={(e) => handleMinPrice(e)}
            value={minPriceRange}
            min="0"
            max="999999"
            step="1"
          />
          <input
            className="thumb-2"
            type="range"
            name="range-2"
            id="range-2"
            onChange={(e) => handleMaxPrice(e)}
            value={maxPriceRange}
            min="0"
            max="999999"
            step="1"
          />
        </div>

        <p>{minPriceRange}円〜{maxPriceRange}円</p>
      </div>
      <div className="cate_wrap">
        <p>カテゴリー一覧</p>
        <ul>
          {/* カテゴリ表示 */}
          {categoryLists.map((categoryList) => (
            <li>
              <label htmlFor={categoryList.id}>
                <input
                  type="radio"
                  name="category"
                  id={categoryList.id}
                  value={categoryList.category}
                  onChange={(e) => handleChecked(e)}
                />
                {categoryList.categoryName}
              </label>
            </li>
          ))}
          {/* {posts.filter(item => !check.has(item.category) && check.add(item.category)).map((post:any) => (
          <li><label htmlFor={post.category}><input type="radio" name="category" id={post.category} value={post.category} onChange={(e) => handleChecked(e)}/>{post.category}</label></li>
        ))} */}
        </ul>
      </div>
    </div>
  );
};

// https://www.hypertextcandy.com/react-tutorial-04-form-and-events
