import { createSlice } from "@reduxjs/toolkit";
import { collection, getDocs, setDoc } from "firebase/firestore";
import { db } from "../../firebase";

type apiArray = {
  id: number | string;
  // title: string;
  // price: number;
  // image: string;
};

let cartItems: apiArray[] = [];
const cartCollectionRef = collection(db, "cart");
getDocs(cartCollectionRef).then((querySnapshot) => {
  cartItems = querySnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));
});

const initialState: any = {
  cartItems: [],
  newCartItem:[],
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addCart: async (state, action) => {
        await setDoc(cartCollectionRef, {
          id: state.newCartItem.id,
          title: state.newCartItem.title,
          price: state.newCartItem.price,
          image: state.newCartItem.image,
        });
      state.cartItems = cartItems;
        state.newCartItem = action.payload;
    //   await setDoc(cartCollectionRef, state.newCartItem);

      console.log(cartCollectionRef);
    },
  },
});

export const { addCart } = cartSlice.actions;
export default cartSlice.reducer;
