import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

// type apiArray = {
//   userId: number,
//   id: number,
//   title: string,
// }

export const ItemDetail = () => {
  const {productId} = useParams();

  const [posts, setPosts] = useState<any>([])

  const apiItems = async(id:any) => {
    const response = await axios
    .get(`https://fakestoreapi.com/products/${id}`)
    .then((res) => {
      setPosts(res.data)
    })
    .catch((err) => {
      console.log('エラー', err);
    });
  }  
  useEffect(() => {
     apiItems(productId)

  },[])



  return (
    <div className="container">
      {/* <div>...Loading</div> */}
      <div className="">
        <div className="">
          <div className="flex">
            <div className="item">
              <img className='' src={posts.image} />
            </div>
            <div className="">
              <h1>{posts.title}</h1>
              <h2>
                <a className="">{posts.price * 100}円</a>
              </h2>
              <h3 className="">カテゴリー：{posts.category}</h3>
              <p>{posts.description}</p>
              <div className="">
                <div className=" ">
                  <i className=" "></i>
                </div>
                <div className="">Add to Cart</div>
              </div>
            </div>
          </div>
        </div>
      </div>
  </div>
  )
}
