export const Header = () => {



  const [totalAmount, setTotalAmount] = useState<any>([]);



  return (
    <header className="header">
      <div className="container flex">
        <Link to='/'>
          <h2>デモECサイト</h2>        
        </Link>
        <div className="cart-logo">
          <Link to='cart'>
            <ShoppingCartIcon/>
            <span className="cart-logo__amount">
            {totalAmount.length}
            </span>          
          </Link>
        </div>
      </div>
    </header>
  );
};
