import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [],
};

const itemSlice = createSlice({
  name: "item",
  initialState,
  reducers: {
      setPosts: (state, action) => {
          state.items = action.payload
        },
    },
});



  export const {setPosts} = itemSlice.actions;
export default itemSlice.reducer;
