import { createSlice } from "@reduxjs/toolkit";

type apiArray = {
  id: number | string;
  // title: string;
  // price: number;
  // image: string;
};



let arr:any = localStorage.getItem('cartItems');



const initialState: any = {
  cartItems: [],
  totalPrice: '',
  totalAmount: 0,
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addCart: async (state, action) => {

      // console.log(localStorage.getItem('cartItems'));
      let current = [{...state.cartItems}]
      if(arr !== null){
        current= JSON.parse(arr);       
      }
      current.push(action.payload);
      let cartArray:any = JSON.stringify(current);
      localStorage.setItem('cartItems', cartArray);
      arr = localStorage.getItem('cartItems');
      state.cartItems = arr;

      await window.location.reload();

      alert('カートへ追加が完了しました。')
      state.cartDone = true;

    },
    removeAll: async (state) => {
      localStorage.clear();
      state.cartItems = [];
      window.location.reload();

    },
    removeItem:(state) => {

    },
    handleCart:(state) => {
      arr = localStorage.getItem('cartItems');
      if(arr !== null){
        state.cartItems = JSON.parse(arr);
        state.cartItems.shift();
      }
    },
    handleTotalAmount: (state) => {
      let current = [{...state.cartItems}]
      if(arr !== null){current= JSON.parse(arr);}
      // let totalAmount = 0;
      // let totalPrice = 0;
      // console.log(current);

      // current.forEach((item:any) => {
      //    totalAmount += item.length;
      //   });
        state.totalAmount = current.length;


    },
    handleTotalPrice: (state) => {
      let total = 0;
      state.cartItems.forEach((item:any) => {
        total += item.price;
      });
      //新しい状態に更新
      state.totalPrice = total;
    }
  },
});

export const { addCart, removeAll ,removeItem, handleCart,handleTotalAmount,handleTotalPrice } = cartSlice.actions;
export default cartSlice.reducer;
