import axios from "axios";
import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import Item from "./Item";
import { Sidebar } from "./Sidebar";

export const ItemsLists = () => {
  type apiArray = {
    id: number;
    title: string;
    price: number;
    category: string;
    image: string;
  };

  const [posts, setPosts] = useState([]);
  const [filteredUser, setFilteredUser] = useState<apiArray[]>([]);
  const [filterFlag, setFilterFlag] = useState(false);
  const [searchParams] = useSearchParams();
  const categoryParams: any = searchParams.get("category");

  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };

  useEffect(() => {
    apiItems();
  }, []);

  useEffect(() => {
    let copyPosts = [...posts];
    if (categoryParams) {
      copyPosts = copyPosts.filter((post: apiArray) =>
        post.category.match(categoryParams)
      );
      setFilterFlag(true);
    }

    setFilteredUser(copyPosts);
  }, [categoryParams]);

  return (
    <div className="container item_lists_wrap flex">
      <Sidebar />
      {}
      <div className="item_lists flex">
        {filterFlag
          ? filteredUser.map((item: apiArray) => {
              return <Item key={item.id} {...item} />;
            })
          : posts.map((item: apiArray) => {
              return <Item key={item.id} {...item} />;
            })}
      </div>
    </div>
  );
};
