  id: number;
  title: string;
  price: number;
  category: string;
  image: string;
};

export const ItemDetail = ({ id, title, price, category, image }: apiArray) => {
  const {productId} = useParams();

  const [posts, setPosts] = useState([])

  const apiItems = async(id:any) => {
    const response = await axios
    .get(`https://fakestoreapi.com/products/${id}`)
