import { createSlice } from "@reduxjs/toolkit";
import { collection, doc, getDocs, setDoc } from "firebase/firestore";
import { db } from "../../firebase";

type apiArray = {
  id: number | string;
  // title: string;
  // price: number;
  // image: string;
};

let cartItems: apiArray[] = [];


const cartCollectionRef = collection(db, "cart");
getDocs(cartCollectionRef).then((querySnapshot) => {
  cartItems = querySnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }));

});

const initialState: any = {
  cartItems: [],
  totalAmount: cart
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    addCart: async (state, action) => {

      state.cartItems = cartItems;
      const cartDocumentRef = doc(collection(db, "cart"));
      const newCartItem = action.payload;
      await setDoc(cartDocumentRef, newCartItem);
      //   await setDoc(cartCollectionRef, state.newCartItem);

    //   console.log(cartDocumentRef);
    },
    handleCart: (state) => {
      state.cartItems = cartItems;
    }
  },
});

export const { addCart, handleCart } = cartSlice.actions;
export default cartSlice.reducer;
