import { Backdrop, Button, CircularProgress } from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getItems } from "../redux/item/itemSlice";

type apiDetailArray = {
  id: number;
  title: string;
  image: string;
  price: number;
  category: string;
  description: string;
};

export const ItemDetail = () => {
  const dispatch = useDispatch();
  const {items} = useSelector((state:any) => state.items)
  const { productId } = useParams();
  const open:boolean = true;
  const [posts, setPosts] = useState<apiDetailArray>();


  useEffect(() => {
    dispatch(getItems());

  }, [productId]);


  return (
    <div className="container">
      {/* {Object.keys(posts).length === 0 ? ( */}

      {posts ? (
        <div>
          {/* {posts.map((post) =>  ( */}
          {
            <div className="detail flex" key={items.id}>
              <div className="detail__image">
                <img src={items.image} />
              </div>
              <div className="detail__title">
                <h1 className="detail__title__text">{posts.title}</h1>
                <h2 className="detail__title__text">
                  <a >{posts.price * 100}円</a>
                </h2>
                <h3 className="detail__title__text">カテゴリー：{posts.category}</h3>
                <p>{posts.description}</p>
                <div className="detail__title__text">
                  <Button variant="contained" className="detail__cart_btn">
                    カートに入れる
                  </Button>
                </div>
              </div>
            </div>
          }
        </div>
      ) : (
        <Backdrop open={open}>
        <CircularProgress color="inherit" />
      </Backdrop>
        // <div>...Loading</div>
      )}
    </div>
  );
};
