import { Button } from "@mui/material";
import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import {
  handleCart,
  handleTotalPrice,
  removeAll,
  removeItem,
} from "../../redux/cart/cartSlice";
import { AppDispatch, useSelector } from "../../store";
import CartItem from "./CartItem";

function CartContainer() {
  type cartArray = {
    id: number;
    title: string;
    image: string;
    price: number;
    category: string;
    description: string;
  };

  const dispatch = useDispatch<AppDispatch>();
  const { cartItems, totalPrice } = useSelector((store) => store.cart);

  useEffect(() => {
    dispatch(handleCart());
    dispatch(handleTotalPrice());
  }, []);

  if (totalPrice < 1 || totalPrice === undefined) {
    return (
      <section className="cart">
        <div>
          <h2>買い物かご</h2>
          <h4 className="empty-cart">何も入ってません・・・</h4>
        </div>
      </section>
    );
  }

  return (
    <section className="cart">
      <div>
        <h2>買い物かご</h2>
      </div>
      <div>
        {cartShow
          ? cartItems.map((item: cartArray) => {
              return <CartItem key={item.id} {...item} />;
            })
          : ""}

        {/* {cartItems
          ? cartItems.map((item: cartArray) => {
              return <CartItem key={item.id} {...item} />;
            })
          : ""} */}
      </div>
      <footer>
        <hr />
        <div className="cart-total">
          <h4>
            合計 <span>{Math.floor(totalPrice * 100)}円</span>
          </h4>
        </div>
        <div className="clear-btn">
          <Button
            className="btn "
            variant="outlined"
            color="error"
            onClick={() => dispatch(removeItem())}
          >
            選択した商品を削除
          </Button>
          <Button className="btn " onClick={() => dispatch(removeAll())}>
            全削除
          </Button>
        </div>
      </footer>
    </section>
  );
}

export default CartContainer;
