import axios from "axios";
import React, { useEffect, useState } from "react";

export const Sidebar = () => {
  type apiArray = {
    category: string;
  };

  const [posts, setPosts] = useState<apiArray[]>([]);
  const [categoryList, setCategoryList] = useState<any>([]);
  var check = new Set();


  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
        // カテゴリ一覧表示
        // const categoryFilter = new Map(res.data.map( (item:any)=> [item.category, item]))

      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };

  useEffect(() => {
    apiItems();

  }, []);

  // useEffect(() => {
  //   let categoryFilter = new Map(posts.map( (item:any)=> [item.category, item]))
  //   setCategoryList(categoryFilter);
  //   console.log(categoryList);
  // }, [posts]);



  return (
    <div className="sidebar">
      <div className="cate_wrap">
        <p>カテゴリー一覧</p>
        <ul>


          {posts.filter(item => !new Set.has(item.category && new Set.add(item.category))).map((post:any) => (
          <li>{post.category}</li>
        ))}
        </ul>
      </div>
    </div>
  );
};
