import { Button } from '@mui/material';
import { collection, getDocs } from 'firebase/firestore';
import React, { useEffect, useState } from 'react'
import { useDispatch } from 'react-redux';
import {  handleTotalPrice, removeAll } from '../../redux/cart/cartSlice';
import { AppDispatch, useSelector } from '../../store';
import CartItem from './CartItem';

function CartContainer() {
    type cartArray = {
        id: number;
        title: string;
        image: string;
        price: number;
        category: string;
        description: string;
    };


    const dispatch = useDispatch<AppDispatch>();
    const {totalPrice} = useSelector((state) => state.cart);
    const [cartItems, setCartItems] = useState<any>([]);


    useEffect(() => {
      const cartCollectionRef = collection(db, "cart");
       getDocs(cartCollectionRef).then((querySnapshot) => {
      setCartItems(
        querySnapshot.docs.map((doc) => ({ ...doc.data(), id: doc.id }))
        )
      });

      handleTotalPrice();

    },[]);


  return (
    <section className="cart">
    <header>
      <h2>買い物かご</h2>
    </header>
    <div>
      {cartItems.map((item:cartArray) => {
        return <CartItem key={item.id} {...item} />;
      })}
    </div>
    <footer>
      <hr />
      <div className="cart-total">
        <h4>
          合計 <span>{totalPrice}円</span>
        </h4>
      </div>
      <Button className="btn clear-btn" onClick={() => dispatch(removeAll())}>全削除</Button>

    </footer>
  </section>
  )
}

export default CartContainer;
