import axios from "axios";
import React, { useEffect, useState } from "react";
import { createSearchParams, Navigate, useNavigate, useSearchParams } from "react-router-dom";

export const Sidebar = () => {

  type apiArray = {
    category: string;
  };

  const categoryLists = [
    {
      id : '1',
      category:'men\'s clothing'
    },
    {
      id : '2',
      category:'jewelery'
    },
    {
      id : '3',
      category:'electronics'
    },
    {
      id : '4',
      category:'women\'s clothing'
    },
  ]

  var check = new Set();
  const navigate = useNavigate();
  const [posts, setPosts] = useState<apiArray[]>([]);


  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };

  // カテゴリ絞り込み
  const handleChecked = (e: React.ChangeEvent<HTMLInputElement>) => {
    let checkedText = e.target.id;
    const params = createSearchParams({
      category: checkedText,
    }).toString();
    navigate(`/category/${params}`,{replace: false});
  }

  useEffect(() => {
    apiItems();
  }, []);

  useEffect()



  return (
    <div className="sidebar">
      <div className="cate_wrap">
        <p>カテゴリー一覧</p>
        <ul>
          {/* カテゴリ表示 */}
          {categoryLists.map((categoryList) => (
             <li><label htmlFor={categoryList.id}><input type="radio" name="category" id={categoryList.id} value={categoryList.category} onChange={(e) => handleChecked(e)}/>{categoryList.category}</label></li>
          ))}
          {/* {posts.filter(item => !check.has(item.category) && check.add(item.category)).map((post:any) => (
          <li><label htmlFor={post.category}><input type="radio" name="category" id={post.category} value={post.category} onChange={(e) => handleChecked(e)}/>{post.category}</label></li>
        ))} */}
        </ul>
      </div>
    </div>
  );
};


