import { createSlice } from "@reduxjs/toolkit"

const initialState = {
    cartItems: []
}

const cartSlice = createSlice({
    name: 'cart',
    initialState,
    reducers: {
        addCart:(state, action) => {
            const detail = action
            state.cartItems.push(action.payload)
        },
    }
})

export default cartSlice.reducer;
