import { Backdrop, Button, CircularProgress } from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

type apiDetailArray = {
  id: number;
  title: string;
  image: string;
  price: number;
  category: string;
  description: string;
};

export const ItemDetail = () => {
  const { productId } = useParams();
  const open:bool = true;
  const [posts, setPosts] = useState<apiDetailArray>();

  const apiItems = async (id: string | undefined) => {
    const response = await axios
      .get(`https://fakestoreapi.com/products/${id}`)
      .then((res) => {
        // const newPosts: apiDetailArray = {
        //   title: res.data.title,
        //   id: res.data.id,
        //   image: res.data.image,
        //   price: res.data.price,
        //   category: res.data.category,
        //   description: res.data.description,
        // };
        setPosts(res.data);
        // console.log(posts);


      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };
  useEffect(() => {
    apiItems(productId);

  }, [productId]);


  return (
    <div className="container">
      {/* {Object.keys(posts).length === 0 ? ( */}

      {posts ? (
        <div>
          {/* {posts.map((post) =>  ( */}
          {
            <div className="detail flex" key={posts.id}>
              <div className="detail__image">
                <img src={posts.image} />
              </div>
              <div className="detail__title">
                <h1 className="detail__title__text">{posts.title}</h1>
                <h2 className="detail__title__text">
                  <a >{posts.price * 100}円</a>
                </h2>
                <h3 className="detail__title__text">カテゴリー：{posts.category}</h3>
                <p>{posts.description}</p>
                <div className="detail__title__text">
                  <Button variant="contained" className="detail__cart_btn">
                    カートに入れる
                  </Button>
                </div>
              </div>
            </div>
          }
        </div>
      ) : (
        <Backdrop open={open}>
        <CircularProgress color="inherit" />
      </Backdrop>
        // <div>...Loading</div>
      )}
    </div>
  );
};
