import axios from "axios";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useSearchParams } from "react-router-dom";
import Item from "./Item";
import { Sidebar } from "./Sidebar";

export const ItemsLists = () => {
  type apiArray = {
    id: number;
    title: string;
    price: number;
    category: string;
    image: string;
  };

  const dispatch = useDispatch();
  const {items} = useSelector((state:any) => state.item)
  const [posts, setPosts] = useState([]);
  const [filteredUser, setFilteredUser] = useState<apiArray[]>([]);
  const [filterFlag, setFilterFlag] = useState<boolean>(false);
  const [maxPriceRange, setMaxPriceRange] = useState<number>(100000);
  const [minPriceRange, setMinPriceRange] = useState<number>(0);
  const [searchParams] = useSearchParams();
  const categoryParams = searchParams.get("category");

  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };

  // API反映
  useEffect(() => {
    // apiItems();
    dispatch(getPosts());
  }, [dispatch]);


  // 絞り込み
  useEffect(() => {
    let copyPosts = [...posts];

    // 料金絞り込み
    if(!minPriceRange && maxPriceRange === 100000){
      setFilterFlag(false);
    } else if(minPriceRange || maxPriceRange){
      copyPosts = copyPosts.filter((post: apiArray) => {
       return (post.price < maxPriceRange/100 && post.price > minPriceRange/100)
      })
      setFilteredUser(copyPosts);
      setFilterFlag(true);

    }

    // カテゴリー絞り込み
    if (categoryParams === "all") {
      // setFilterFlag(false);
      setFilteredUser(copyPosts);
      return;
    } else if (categoryParams) {
      copyPosts = copyPosts.filter((post: apiArray) =>
        post.category.match(categoryParams)
      );
      setFilterFlag(true);
    }
    setFilteredUser(copyPosts);
  }, [categoryParams ,maxPriceRange, minPriceRange]);

  return (
    <div className="container item-lists-wrap flex">
      <Sidebar
        setMaxPriceRange={setMaxPriceRange}
        setMinPriceRange={setMinPriceRange}
      />
      {posts ? (
        <div className="item-lists flex">
          {filterFlag
            ? filteredUser.map((item: apiArray) => {
                return <Item key={item.id} {...item} />;
              })
            : posts.map((item: apiArray) => {
                return <Item key={item.id} {...item} />;
              })}
        </div>
      ) : (

        <div>Loading...</div>
      )}
    </div>
  );
};
