      cartItems.push(action.payload);

      let cartArray = JSON.stringify(cartItems);
      localStorage.setItem('cartItems', cartArray);
      cartArray = localStorage.getItem('cartItems');
      state.cartItems = cartArray;
      console.log(state.cartItems);



