import { Button } from "@mui/material";
import React, { useEffect, useState } from "react";
import {
  createSearchParams,
  useNavigate,
  useSearchParams,
} from "react-router-dom";



// TODO:any
export const Sidebar = ( {setMaxPriceRange, setMinPriceRange}:any) => {


  const navigate = useNavigate();
  const [maxPrice, setMaxPrice] = useState<number>(100000);
  const [minPrice, setMinPrice] = useState<number>(0);
  const [searchParams, setSearchParams] = useSearchParams();
  let params = "";

  // カテゴリ絞り込み
  const handleChecked = (e: React.ChangeEvent<HTMLInputElement>) => {
    let checkedId = e.target.id;
    let checkedName = e.target.value;
    params = createSearchParams({
      categoryId: checkedId,
      category: checkedName,
    }).toString();
    navigate(`?${params}`, { replace: false });
  };

  useEffect(() => {
    setSearchParams(params);
  }, [params]);

  const handleMaxPrice = (e: React.ChangeEvent<HTMLInputElement>) => {
    let maxPriceValue = Number(e.target.value);
    if(minPrice + 1000 > maxPriceValue){
      return
    }
    setMaxPrice(maxPriceValue);

  }

  const handleMinPrice = (e: React.ChangeEvent<HTMLInputElement>) => {
    let minPriceValue = Number(e.target.value);
    if(maxPrice - 1000 < minPriceValue){
      return
    }
    setMinPrice(minPriceValue);
  }

  const ClickPrice = () => {
    setMaxPriceRange(maxPrice);
    setMinPriceRange(minPrice);
  }

  return (
    <div className="sidebar">
      <div className="price-wrap">
        <p>値段で絞り込む</p>
        <div className="price-wrap__multithumb">
          <input
            className="price-wrap__thumb1"
            type="range"
            name="range-1"
            id="back"
            onChange={(e) => handleMinPrice(e)}
            value={minPrice}
            min="0"
            max="100000"
            step="1000"
          />
          <input
            className="price-wrap__thumb2"
            type="range"
            name="range-2"
            id="front"
            onChange={(e) => handleMaxPrice(e)}
            value={maxPrice}
            min="0"
            max="100000"
            step="1000"
          />
        </div>


        <p>{minPrice}円〜{maxPrice}円</p>
        <Button variant="outlined" onClick={() => ClickPrice()}>料金絞り込み</Button>

      </div>
      <div className="cate-wrap">
        <p>カテゴリー一覧</p>
        <ul>
          {/* カテゴリ表示 */}
          {categoryLists.map((categoryList) => (
            <li key={categoryList.id}>
              <label htmlFor={categoryList.id}>
                <input
                  type="radio"
                  name="category"
                  id={categoryList.id}
                  value={categoryList.category}
                  onChange={(e) => handleChecked(e)}
                />
                {categoryList.categoryName}
              </label>
            </li>
          ))}
          {/* {posts.filter(item => !check.has(item.category) && check.add(item.category)).map((post:any) => (
          <li><label htmlFor={post.category}><input type="radio" name="category" id={post.category} value={post.category} onChange={(e) => handleChecked(e)}/>{post.category}</label></li>
        ))} */}
        </ul>
      </div>
    </div>
  );
};

