import axios from "axios";
import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import Item from "./Item";
import { Sidebar } from "./Sidebar";

export const ItemsLists = () => {
  type apiArray = {
    id: number;
    title: string;
    price: number;
    category: string;
    image: string;
  };

  const [posts, setPosts] = useState([]);
  const [filteredUser, setFilteredUser] = useState<apiArray[]>([]);
  const [filterFlag, setFilterFlag] = useState<boolean>(false);
  const [priceFlag, setPriceFlag] = useState<boolean>(false);
  const [maxPriceRange, setMaxPriceRange] = useState<number>(100000);
  const [minPriceRange, setMinPriceRange] = useState<number>(0);
  const [searchParams] = useSearchParams();
  const categoryParams: any = searchParams.get("category");

  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };

  // API反映
  useEffect(() => {
    apiItems();
  }, []);

  // 料金絞り込み
  useEffect(() => {
    let copyPosts = [...posts];
    if(!minPriceRange && maxPriceRange === 100000){
      setFilterFlag(false);
      return;
    } 
    copyPosts = copyPosts.filter((post: apiArray) => {
     return (post.price < maxPriceRange/100 && post.price > minPriceRange/100)
    })
    setFilterFlag(true);
    setFilteredUser(copyPosts)

  }, [maxPriceRange, minPriceRange]);


  // カテゴリー絞り込み
  useEffect(() => {
    let copyPosts = [...posts];
    console.log(categoryParams);

    if (categoryParams === "all") {
      setFilterFlag(false);
      setFilteredUser(copyPosts);
      return;
    } else if (categoryParams) {
      copyPosts = copyPosts.filter((post: apiArray) =>
        post.category.match(categoryParams)
      );
      setFilterFlag(true);
    }
    setFilteredUser(copyPosts);
  }, [categoryParams]);

  return (
    <div className="container item_lists_wrap flex">
      <Sidebar
        maxPriceRange={maxPriceRange}
        setMaxPriceRange={setMaxPriceRange}
        minPriceRange={minPriceRange}
        setMinPriceRange={setMinPriceRange}
        priceFlag = {priceFlag}

      />
      {posts ? (
        <div className="item_lists flex">
          {filterFlag
            ? filteredUser.map((item: apiArray) => {
                return <Item key={item.id} {...item} />;
              })
            : posts.map((item: apiArray) => {
                return <Item key={item.id} {...item} />;
              })}
        </div>
      ) : (
        <div>Loading...</div>
      )}
    </div>
  );
};
