import { Button } from '@mui/material';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

type apiDetailArray = {
  id: number,
  title: string,
  image: string,
  price: number,
  category: string,
  description: string,
}

export const ItemDetail = () => {
  const {productId} = useParams();
  const [posts, setPosts] = useState<apiDetailArray[]>([])

  const apiItems = async(id:string | undefined) => {
    const response = await axios
    .get(`https://fakestoreapi.com/products/${id}`)
    .then((res) => {
      const newPosts:apiDetailArray = {
        title:res.data.title,
        id: res.data,
        title: string,
        image: string,
        price: number,
        category: string,
        description: string,

      }
      setPosts(newPosts)
    })
    .catch((err) => {
      console.log('エラー', err);
    });

  }  
  useEffect(() => {
     apiItems(productId)

  },[])



  return (
    <div className="container">
      {/* {Object.keys()} */}
      {/* <div>...Loading</div> */}
          <div className="detail flex">
            <div className="item_detail_image">
              <img className='' src={posts.image} />
            </div>
            <div className="item_detail">
              <h1>{posts.title}</h1>
              <h2>
                <a className="">{posts.price * 100}円</a>
              </h2>
              <h3 className="">カテゴリー：{posts.category}</h3>
              <p>{posts.description}</p>
              <div className="">
                <div className=" ">
                  <i className=" "></i>
                </div>
                <Button variant="text" className="cart_btn">Add to Cart</Button>
              </div>
            </div>
          </div>
        </div>
  )
}
