import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";
import axios from "axios";


/** データ取得非同期処理 */
// 投稿ーデータを取得
export const apiItems = createAsyncThunk(
    "apiItems",
    async () => {
      const { data } = await axios.get(
        `https://fakestoreapi.com/products`
      );
      return data;
    }
  );

const initialState = {
    apiItems:[],
}




const itemSlice = createSlice({

    name:'item',
    initialState: initialState,
    reducers: {},
    extraReducers: (builder) => {
      builder.addCase(apiItems.fulfilled, (state, action) => {
        return {
          ...state,
          apiItems: action.payload,
        };
      });
      builder.addCase(apiItems.rejected, (state, action) => {
        return {
          ...state,
          apiItems: initialState.apiItems,
        };
      });
    },
})


export const selectItems = (state) => state.item.apiItems;

export default itemSlice.reducer;
