import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const getItems:any = createAsyncThunk('items/getItems', async () => {
    return await fetch('https://fakestoreapi.com/products').then((res) =>
      res.json()
    );
  });

  export const getDetail:any = createAsyncThunk('items/getDetail', async (id: string | undefined) => {
    return await fetch(`https://fakestoreapi.com/products/${id}`).then((res) =>
      res.json()
    );
  });

const initialState = {
  items: [],
  detail: [],
  maxPrice:100000,
  minPrice:0,
};

const itemSlice = createSlice({
  name: "items",
  initialState,
  reducers:{
    handleMaxPrice: (state, action) => {
      let maxPriceValue = Number(action.payload);
      if(state.minPrice + 1000 > maxPriceValue){
        return
      }
      state.maxPrice = maxPriceValue;
      console.log(state.maxPrice);
    },
    handleMinPrice: (state, action) => {
      let minPriceValue = Number(action.payload);
      if(state.maxPrice - 1000 > minPriceValue){
        return
      }
      state.minPrice = minPriceValue;
      console.log(state.minPrice);
    },
  },
  extraReducers: {
    [getItems.fulfilled]: (state:any, action:any) => {
      state.items = action.payload;
    },
    [getDetail.fulfilled]: (state:any, action:any) => {
        state.detail = action.payload;
    },
  },
});


export const {handleMaxPrice, handleMinPrice} = itemSlice.actions;
export default itemSlice.reducer;
