import { Backdrop, Button, CircularProgress } from "@mui/material";
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getDetail } from "../redux/item/itemSlice";

type apiDetailArray = {
  id: number;
  title: string;
  image: string;
  price: number;
  category: string;
  description: string;
};

export const ItemDetail = () => {
  const dispatch = useDispatch();
  const {detail} = useSelector((state:any) => state.items)
  const { productId } = useParams();
  const open:boolean = true;
  const [posts, setPosts] = useState<apiDetailArray>();

  // const apiItems = async (id: string | undefined) => {
  //   const response = await axios
  //     .get(`https://fakestoreapi.com/products/${id}`)
  //     .then((res) => {
  //       setPosts(res.data);
  //     })
  //     .catch((err) => {
  //       console.log("エラー", err);
  //     });
  // };
  useEffect(() => {
    dispatch(getDetail(productId));

  }, [productId]);


  return (
    <div className="container">
      {/* {Object.keys(posts).length === 0 ? ( */}

      {detail ? (
        <div>
          {/* {posts.map((post) =>  ( */}
          {
            <div className="detail flex" key={detail.id}>
              <div className="detail__image">
                <img src={detail.image} />
              </div>
              <div className="detail__title">
                <h1 className="detail__title__text">{detail.title}</h1>
                <h2 className="detail__title__text">
                  <a >{detail.price * 100}円</a>
                </h2>
                <h3 className="detail__title__text">カテゴリー：{detail.category}</h3>
                <p>{detail.description}</p>
                <div className="detail__title__text">
                  <Button variant="contained" className="detail__cart_btn">
                    カートに入れる
                  </Button>
                </div>
              </div>
            </div>
          }
        </div>
      ) : (
        <Backdrop open={open}>
        <CircularProgress color="inherit" />
      </Backdrop>
        // <div>...Loading</div>
      )}
    </div>
  );
};
