import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  posts: [],
};

const itemSlice = createSlice({
  name: "item",
  initialState,
  reducers: {
    setItems: (state, action) => {
        state.posts
    },
  },

});

export default itemSlice.reducer;
