import axios from 'axios';
import React, { useEffect, useState } from 'react'

export const Sidebar = () => {

  type apiArray = {
    category: string;
  };

  const [posts, setPosts] = useState<apiArray[]>([])

  const apiItems = async() => {
    const response = await axios
    .get('https://fakestoreapi.com/products')
    .then((res) => {
      setPosts(res.data)
    }
    )
    .catch((err) => {
      console.log('エラー', err);
    });
  }  
  // let categoryLists = []
  // let categoryList  = posts.find((post) => { return post.category;} );
  let categoryList  = posts.map((post) => post.find((item) => {return item.cate}))





  useEffect(() => {
    apiItems()
    console.log(categoryList);

  },[])

  return (
    <div className="sidebar">
      <div className="cate_wrap">
      <p>
      カテゴリー一覧
      </p>
      <ul>

        {posts.map((post) => (
          <li>{post.category}</li>
        ))}
      </ul>
      </div>

    </div>
  )
}
