import axios from "axios";
import React, { useEffect, useState } from "react";

export const Sidebar = () => {
  type apiArray = {
    category: string;
  };

  const [posts, setPosts] = useState<apiArray[]>([]);
  const [categoryList, setCategoryList] = useState<apiArray[]>([]);

  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
        // カテゴリ一覧表示
        const categoryFilter = new Map(posts.map( (item:any)=> [item.category, item]))
        console.log(categoryFilter);

        setCategoryList(categoryFilter)

      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };
  // let categoryLists = []
  // let categoryList  = posts.find((post) => { return post.category;} );

  useEffect(() => {
    apiItems();

  }, []);




  return (
    <div className="sidebar">
      <div className="cate_wrap">
        <p>カテゴリー一覧</p>
        <ul>


          {categoryList.map((post) => (
          <li>{post.category}</li>
        ))}
        </ul>
      </div>
    </div>
  );
};
