import axios from "axios";
import React, { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import Item from "./Item";
import { Sidebar } from "./Sidebar";

export const CategoryLists = () => {
  type apiArray = {
    id: number;
    title: string;
    price: number;
    category: string;
    image: string;
  };

  let [posts, setPosts] = useState([]);
  let url = window.location.href;
  const [filteredUser, setFilteredUser] = useState<User[]>([]);
  const [searchParams, setSearchParams] = useSearchParams();
  const categoryParams: any = searchParams.get("category");

  // console.log(categoryParams);


  const apiItems = async () => {
    const response = await axios
      .get("https://fakestoreapi.com/products")
      .then((res) => {
        setPosts(res.data);
      })
      .catch((err) => {
        console.log("エラー", err);
      });
  };

  useEffect(() => {
    apiItems();
  }, []);

  useEffect(() => {
    if(categoryParams){
      let copyPosts = [...posts]
      copyPosts = copyPosts.filter((post: apiArray) =>
        post.category.match(categoryParams)

      );
      console.log(copyPosts);
      setPosts([...copyPosts])

    }

  }, [url]);

  return (
    <div className="container item_lists_wrap flex">
      <Sidebar />
      <div className="item_lists flex">
        {/* {
        posts.filter((item) => {
          return 
        }).map((item:apiArray) => {
          // console.log(item);
            return <Item key={item.id} {...item}/>

        })}    */}
        {posts.map((item: apiArray) => {
          return <Item key={item.id} {...item} />;
        })}
      </div>
    </div>
  );
};
