import { Button } from '@mui/material';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';

type apiDetailArray = {
  id: number,
  title: string,
  image: string,
  price: number,
  category: string,
  description: string,
}

export const ItemDetail = () => {
  const {productId} = useParams();
  const [posts, setPosts] = useState<apiDetailArray[]>([])
  const titile = posts.title;

  const apiItems = async(id:string | undefined) => {
    const response = await axios
    .get(`https://fakestoreapi.com/products/${id}`)
    .then((res) => {
      setPosts(res.data)
    })
    .catch((err) => {
      console.log('エラー', err);
    });

  }  
  useEffect(() => {
     apiItems(productId)

  },[])



  return (
    <div className="container">
      {/* {Object.keys()} */}
      {/* <div>...Loading</div> */}
      {posts.map((post) => (
          <div className="detail flex">
            <div className="item_detail_image">
              <img className='' src={post.image} />
            </div>
            <div className="item_detail">
              <h1>{post.title}</h1>
              <h2>
                <a className="">{post.price * 100}円</a>
              </h2>
              <h3 className="">カテゴリー：{post.category}</h3>
              <p>{post.description}</p>
              <div className="">
                <div className=" ">
                  <i className=" "></i>
                </div>
                <Button variant="text" className="cart_btn">Add to Cart</Button>
              </div>
            </div>
          </div>

      ))}
        </div>
  )
}
